INSERT INTO `fa_hotel` VALUES (48, 2, 'MEHOOD-JSXT', 'I', '0', '美豪酒店嘉善银泰店', '美豪酒店嘉善银泰店', '美豪酒店嘉善银泰店', 'CN', '嘉兴', '嘉善县嘉善大道与阳光路交叉口东北角', '', '0573-84822888', '0573-84822888', '0573-84822888', '', '', '美豪酒店(嘉善西塘店) 隶属于上海美豪酒店集团旗下，是一家重视亲情化服务的精致商旅酒店。美豪酒店曾荣获2015 年度“中国酒店影响力品牌”、2016年度“中国酒店业优质服务奖”、2017 “中国新锐酒店品牌”等荣誉。 \r美豪酒店(嘉善西塘店)地处嘉善新城核心，座落于嘉善县置地广场(嘉善大道与阳光路交叉口东北角，酒店周边交通便利，距嘉善县政府200米，距沪杭高速大云出口及嘉善高铁南站仅5分钟车程，距“活着的千年古镇” 西塘15分钟车程。\r酒店周边商业繁荣，酒店楼下就是全国著名的银泰百货，300 米范围内云集了太平洋百货、万联城购物中心、物美超市等商业配套设施。 \r美豪酒店(嘉善西塘店)配有101间客房，设计风格时尚雅致。3D影院客房配置 3D投影和立体环绕音响，在震撼影音中带您穿越电影时空。酒吧茶餐厅装修典雅时尚，是商务小憩、读心品茗、休闲会友的俱佳之地。酒店提供免费自助洗衣房，为您疲惫的旅途带来一份清爽洁净。 美豪酒店(嘉善西塘店)致力为顾客创造“满意+惊喜”的超值服务！\r', 0, 'WEB', 'Jul 13, 2017 1:31:50 PM', '15251528978', 'Oct 10, 2018 11:43:38 AM', 'ZJ', 'JXZJ', '330400', '0', '7', 'PP001', '0.0', '4', 'T', 'IHOTEL', 'IHOTEL', '3', '服务器组美豪', '4', NULL, NULL);
