INSERT INTO `fa_hotel` VALUES (72, 2, 'MEHOOD-HS', 'I', '0', '美豪酒店黄山屯溪老街店', '美豪酒店黄山屯溪老街店', '美豪酒店黄山屯溪老街店', 'CN', '黄山', '安徽省黄山市屯溪区迎宾大道10号', '', '0559-2577777', '0559-2577777', '0559-2577777', '', '', '美豪酒店是上海美豪酒店管理股份有限公司旗下投资管理的酒店。美豪酒店致力于打造酒吧文化+3D影院与酒店完美结合的风情主题酒店，创新以智能时尚、简约原生态装修、异国风情全时茶餐吧、五星核心设施配置、自助洗衣房、3D影院客房、个性管家服务等全新产品模式，为商旅顾客带来舒适、超值的入住体验，在静谧优雅环境中得到心灵的释放及家一样的温馨感受。酒店拥有客房121间，坐拥在被誉为“天下第一奇山”的安徽黄山，位于安徽省南部黄山市黄山区境内，南北长约40公里，东西宽约30公里，山脉总面积1200平方公里，核心景区面积约160.6平方公里，面积1078平方公里。黄山为“三山五岳”中三山之一，以天都峰、莲花峰、光明顶三大主峰为中心向周围延展的连绵不断的山脉，蜿蜒曲折，蔚为壮观。黄山以奇松、怪石、云海、温泉“四绝”著称，将各大名山的特色融为一体，浑然天成。\r', 0, 'WEB', 'Mar 20, 2018 10:12:40 AM', 'ADMIN', 'Jan 11, 2019 12:54:39 PM', 'AN', 'HSAN', '341003', '0', '7', 'PP001', '0.0', '4', 'T', 'IHOTEL', 'IHOTEL', '3', '服务器组美豪', '4', NULL, NULL);
