INSERT INTO `fa_hotel` VALUES (112, 2, 'MEHOOD-WLMQGTZ', 'I', '0', '美豪丽致酒店乌鲁木齐高铁站店', '美豪丽致酒店乌鲁木齐高铁站店', '美豪丽致酒店乌鲁木齐高铁站店', 'CN', '新疆维吾尔自治区/乌鲁木齐市/头屯河区', '乌鲁木齐 头屯河区 黄山街81号5号楼1座901室', '', '0991-3764888', '', '', '', '', '美豪·丽致酒店(乌鲁木齐高铁站店)是美豪酒店集团历时七年研发，精耕细作，为中高端商旅人士打造的城市度假酒店，原生态设计，智能生活体验，高级床品与洗护套装，完善的多维功能空间配套，免费开放的城市会素雅和宁静.客厅，繁华都市中静瑟守候，管家贴心服务让您尊享商旅度假体验。酒店客房风格集现代简约、古典时尚，精致典雅，均以品味奢华的用料以及自然的木饰装点。酒店配有中餐厅、自助餐厅和咖啡吧等完善配套，更契合商务旅行人士的出行需要。此外酒店还提供超过30个停车位，为出行带来更多方便。酒店整体简约时尚的装修格调，让您身心在紧张、高节奏的工作之余，让自己心情愉悦。', 0, 'ADMIN', '1554971800', '18066858815', '1562990017', 'XJ', 'WLMQ', '650106', '', '4', 'PP002', '0.0', '4', 'T', 'IHOTEL', 'IHOTEL', '2', '服务器组美豪', '4', '', '');
